// src/components/ResultPage.js
import React, { useEffect, useState } from "react";
import { checkOrganMatchOnBlockchain } from "../utils/web3";

function ResultPage() {
  const [status, setStatus] = useState("pending");
  const [matchData, setMatchData] = useState(null);

  useEffect(() => {
    async function fetchMatchResult() {
      const result = await checkOrganMatchOnBlockchain();
      setStatus(result.status);
      setMatchData(result.data);
    }

    fetchMatchResult();
  }, []);

  let content;

  if (status === "pending") {
    content = <p>üïí Matching is under process. Please check after some time...</p>;
  } else if (status === "match") {
    content = (
      <div>
        <h2>‚úÖ Match Found!</h2>
        <p><strong>Name:</strong> {matchData.name}</p>
        <p><strong>Age:</strong> {matchData.age}</p>
        <p><strong>Blood Group:</strong> {matchData.blood_group}</p>
        <p><strong>Organ:</strong> {matchData.organ_type}</p>
        {/* Add more fields as needed */}
      </div>
    );
  } else if (status === "no_match") {
    content = (
      <div>
        <h2>‚ùå No Matches Found</h2>
        <p>
          We know the importance of time and that it is crucial, but currently no matches are found.
          Please check back later.
        </p>
      </div>
    );
  }

  return (
    <div style={{ padding: "40px", textAlign: "center", fontFamily: "Arial" }}>
      {content}
    </div>
  );
}

export default ResultPage;
