// src/components/ResultPage.js
import React, { useEffect, useState } from "react";
import Web3 from "./web3";
import "./ResultPage.css";

function ResultPage() {
  const [matchStatus, setMatchStatus] = useState("pending"); // pending, match_found, no_match
  const [matchData, setMatchData] = useState(null);

  useEffect(() => {
    const fetchBlockchainData = async () => {
      try {
        const web3 = new Web3(Web3.givenProvider || "http://localhost:8545");

        // Simulate a delay and fake match result
        await new Promise(resolve => setTimeout(resolve, 3000)); // Simulate loading

        // Simulated data (since ML logic is not ready yet)
        const isMatchFound = true; // Set to false to test "no match" state

        if (isMatchFound) {
          setMatchData({
            name: "John Doe",
            blood_group: "O+",
            organ_type: "Kidney",
            location: "Mumbai",
            hla_typing: "A1, B8, DR3"
          });
          setMatchStatus("match_found");
        } else {
          setMatchStatus("no_match");
        }
      } catch (error) {
        console.error("Error connecting to blockchain:", error);
        setMatchStatus("no_match");
      }
    };

    fetchBlockchainData();
  }, []);

  let content;
  if (matchStatus === "pending") {
    content = <p className="pending">Matching is under process... Please check after some time.</p>;
  } else if (matchStatus === "match_found" && matchData) {
    content = (
      <>
        <h2>✅ Match Found!</h2>
        <p><strong>Name:</strong> {matchData.name}</p>
        <p><strong>Blood Group:</strong> {matchData.blood_group}</p>
        <p><strong>Organ Type:</strong> {matchData.organ_type}</p>
        <p><strong>Location:</strong> {matchData.location}</p>
        <p><strong>HLA Typing:</strong> {matchData.hla_typing}</p>
      </>
    );
  } else {
    content = (
      <div className="no-match">
        <h2>⚠️ No Matches Found</h2>
        <p>We know the importance of time and that it is crucial,</p>
        <p>but currently no matches found. Please check again later.</p>
      </div>
    );
  }

  return (
    <div className="result-container">
      {content}
    </div>
  );
}

export default ResultPage;
