// src/components/FormPage.js
import React, { useState } from "react";
import axios from "axios";
import "../styles/FormPage.css";
import { useAuth0 } from "@auth0/auth0-react";

function FormPage() {
  const { user, isAuthenticated, isLoading } = useAuth0();
  const [userType, setUserType] = useState(""); // "donor" or "receiver"
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    phone_number: "",
    blood_group: "",
    organ_type: "",
    hla_typing: "",
    bmi: "",
    organ_size: "",
    infection_status: "",
    location: "",
    health_condition: "",
    urgency: "",
    waiting_time: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!isAuthenticated || !user || isLoading) {
      alert("Please log in to register.");
      return;
    }

    try {
      const auth0Id = user.sub; // Get the Auth0 ID
      const endpoint =
      userType === "donor"
    ? "http://localhost:5000/register/donor"
    : "http://localhost:5000/register/receiver";

      // Include auth0Id in the payload
      const payload = { ...formData, auth0Id };
      await axios.post(endpoint, payload);

      alert(`${userType === "donor" ? "Donor" : "Receiver"} Registered Successfully!`);
    } catch (error) {
      console.error(`Error submitting ${userType} data:`, error);
    }
  };

  return (
    <div className="form-container">
      <h2>Register as a Donor or Recipient</h2>

      {/* Donor / Receiver Selection */}
      <div className="user-selection">
        <button
          className={userType === "donor" ? "active" : ""}
          onClick={() => setUserType("donor")}
        >
          Register as Donor
        </button>
        <button
          className={userType === "receiver" ? "active" : ""}
          onClick={() => setUserType("receiver")}
        >
          Register as Receiver
        </button>
      </div>

      {/* Show Form Only After Selection */}
      {userType && (
        <form className="user-form" onSubmit={handleSubmit}>
          <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
          <input type="number" name="age" placeholder="Age" onChange={handleChange} required />
          <input type="text" name="phone_number" placeholder="Phone Number" onChange={handleChange} required />
          <input type="text" name="blood_group" placeholder="Blood Group" onChange={handleChange} required />
          <input type="text" name="organ_type" placeholder="Organ Type" onChange={handleChange} required />
          <input type="text" name="hla_typing" placeholder="HLA Typing" onChange={handleChange} required />
          <input type="number" name="bmi" placeholder="BMI" onChange={handleChange} required />
          <input type="text" name="infection_status" placeholder="Infection Status" onChange={handleChange} />
          <input type="text" name="location" placeholder="Geographic Location" onChange={handleChange} required />
          <input type="text" name="health_condition" placeholder="Health Condition" onChange={handleChange} required />

          {userType === "donor" && (
            <input type="text" name="organ_size" placeholder="Organ Size" onChange={handleChange} required />
          )}

          {userType === "receiver" && (
            <>
              <select name="urgency" onChange={handleChange} required>
                <option value="">Select Urgency</option>
                <option value="low">Low</option>
                <option value="medium">Medium</option>
                <option value="high">High</option>
              </select>
              <input type="number" name="waiting_time" placeholder="Waiting Time (in days)" onChange={handleChange} required />
            </>
          )}

          <button type="submit" className="submit-btn">
            Register {userType === "donor" ? "Donor" : "Receiver"}
          </button>
        </form>
      )}
    </div>
  );
}

export default FormPage;
