// src/components/ResultPage.js
import React, { useEffect, useState } from "react";
import '../styles/ResultPage.css';

function ResultPage() {
  const [status, setStatus] = useState("processing");
  const [matchDetails, setMatchDetails] = useState(null);
  const [cid, setCid] = useState("QmfUzawjrZWCrhb23WS3dhRZ3co5bzCrJCoeM7gMBUqZyu"); // Replace with dynamic or prop value

  useEffect(() => {
    const fetchMatchStatus = async () => {
      try {
        // Step 1: Fetch matched IDs from IPFS
        const ipfsResponse = await fetch(`https://ipfs.io/ipfs/${cid}`);
        const ipfsData = await ipfsResponse.json();

        if (!ipfsData || !ipfsData.donor_id || !ipfsData.receiver_id) {
          setStatus("no-match");
          return;
        }

        // Step 2: Match found, get receiver details from backend
        const receiverRes = await fetch(`http://localhost:5000/receiver/${ipfsData.receiver_id}`);
        const receiverData = await receiverRes.json();

        if (receiverData && receiverData.receiver_id) {
          setMatchDetails(receiverData);
          setStatus("match");
        } else {
          setStatus("no-match");
        }
      } catch (err) {
        console.error("Error during match fetch:", err);
        setStatus("no-match");
      }
    };

    setTimeout(() => {
      fetchMatchStatus();
    }, 3000);
  }, [cid]);

  const renderContent = () => {
    if (status === "processing") {
      return <p>üîÑ Matching is under process. Please wait...</p>;
    } else if (status === "match" && matchDetails) {
      return (
        <>
          <h2>‚úÖ Match Found!</h2>
          <p><strong>Name:</strong> {matchDetails.name}</p>
          <p><strong>Phone:</strong> {matchDetails.phone_number}</p>
          <p><strong>Organ Type:</strong> {matchDetails.organ_type}</p>
          <p><strong>Location:</strong> {matchDetails.location}</p>
        </>
      );
    } else {
      return (
        <>
          <h2>‚ö†Ô∏è No Matches Found</h2>
          <p>
            We know the importance of time and that it is crucial, but currently no matches are
            found. Please check back later.
          </p>
        </>
      );
    }
  };

  return (
    <div className="result-container">
      <div className="result-content slide-down">
        {renderContent()}
      </div>
    </div>
  );
}

export default ResultPage;
