//src/Header.js
import React from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useNavigate } from "react-router-dom";
import "./Header.css";

function Header() {
  const { loginWithRedirect, isAuthenticated, logout, user, getAccessTokenSilently } = useAuth0();
  const navigate = useNavigate();

  const handleDeleteAccount = async () => {
    try {
      const auth0Id = user?.sub;
      const userId = auth0Id?.split("|")[1]; // "D001" or "R001"
  
      if (!auth0Id || !userId) {
        return alert("Invalid user details.");
      }
  
      const response = await fetch("http://localhost:8080/delete-user", {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ auth0Id, userId }),
      });
  
      const result = await response.json();
  
      if (response.ok) {
        alert("Account deleted successfully.");
        logout({ returnTo: window.location.origin }); // Logout from Auth0
      } else {
        alert("Failed to delete account: " + result?.error || "Unknown error");
      }
    } catch (error) {
      console.error("Error deleting account:", error);
      alert("Something went wrong while deleting the account.");
    }
  };
  

  return (
    <header className="header">
      <h1 className="header-logo" onClick={() => navigate("/")}>Organ Tracker</h1>
      <nav className="header-nav">
        {isAuthenticated && <span className="user-welcome">Hi, {user.name}!</span>}
        
        <button onClick={() => navigate("/")} className="nav-btn">Home</button>
        <button onClick={() => navigate("/about")} className="nav-btn">About</button>
        <button onClick={() => navigate("/contact")} className="nav-btn">Contact Us</button>

        
        {isAuthenticated && (
          <button onClick={() => navigate("/result")} className="nav-btn">Results</button>
        )}

        {isAuthenticated ? (
          <>
            <button onClick={() => logout({ returnTo: window.location.origin })} className="auth-btn">Logout</button>
            <button onClick={handleDeleteAccount} className="nav-btn delete-btn">Delete Account</button>
          </>
        ) : (
          <>
            <button 
              onClick={() => loginWithRedirect({ authorizationParams: { screen_hint: "signup", redirect_uri: window.location.origin + "/form" } })} 
              className="auth-btn">
              Sign Up
            </button>

            <button 
              onClick={() => loginWithRedirect({ authorizationParams: { redirect_uri: window.location.origin + "/result" } })} 
              className="auth-btn">
              Login
            </button>
          </>
        )}
      </nav>
    </header>
  );
}

export default Header;
