// src/components/ResultPage.js
import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import "../styles/ResultPage.css";

function ResultPage() {
  const { user, isAuthenticated, isLoading } = useAuth0();
  const [status, setStatus] = useState("processing");
  const [matchDetails, setMatchDetails] = useState(null);

  useEffect(() => {
    const fetchMatchStatus = async () => {
      if (!user) return;
  
      try {
        const auth0Id = user.sub;
  
        // Step 1: Get user role and ID (D001 or R001)
        const userInfoRes = await fetch(`http://localhost:5000/get_user_details/${auth0Id}`);
        const userInfo = await userInfoRes.json();
  
        if (!userInfo.user_id || !userInfo.role) {
          setStatus("no-match");
          return;
        }
  
        // Step 2: Send match request
        const res = await fetch("http://localhost:5000/match", {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify({
            auth0Id,
            role: userInfo.role
          })
        });
  
        const data = await res.json();
  
        // Handle server-side errors
        if (!res.ok) {
          console.error("Server returned an error:", data.error || "Unknown error");
  
          if (data.missing_features) {
            console.error("Missing features:", data.missing_features);
          }
  
          if (data.extra_features) {
            console.error("Extra features:", data.extra_features);
          }
  
          setStatus("no-match");
          return;
        }
  
        if (data.match_found === false) {
          setStatus("no-match");
        } else if (data.cid) {
          const ipfsRes = await fetch(`http://localhost:5000/ipfs/${data.cid}`);
          const matchData = await ipfsRes.json();
  
          setMatchDetails({ ...matchData, cid: data.cid });
          setStatus("match");
        } else {
          setStatus("processing");
        }
      } catch (err) {
        console.error("Error during match fetch:", err);
        setStatus("no-match");
      }
    };
  
    setTimeout(() => {
      fetchMatchStatus();
    }, 3000);
  }, [user]);
  

  const renderContent = () => {
    if (isLoading) return <p>Loading...</p>;

    if (!isAuthenticated) {
      return <p>You need to log in to view your match results.</p>;
    }

    if (status === "processing") {
      return <p>üîÑ Matching is under process. Please wait...</p>;
    } else if (status === "match" && matchDetails) {
      return (
        <>
          <h2>‚úÖ Match Found!</h2>
          <p><strong>Phone:</strong> {matchDetails.phone_number}</p>
          <p><strong>Organ Type:</strong> {matchDetails.organ_type}</p>
          <p><strong>Location:</strong> {matchDetails.location}</p>
          {matchDetails.cid && (
            <p>
              <strong>IPFS Record:</strong>{" "}
              <a
                href={`https://ipfs.io/ipfs/${matchDetails.cid}`}
                target="_blank"
                rel="noopener noreferrer"
              >
                View on IPFS
              </a>
            </p>
          )}
        </>
      );
    } else {
      return (
        <>
          <h2>‚ö†Ô∏è No Matches Found</h2>
          <p>
            We know the importance of time and that it is crucial, but currently no matches are
            found. Please check back later.
          </p>
        </>
      );
    }
  };

  return (
    <div className="result-container">
      <div className="result-content slide-down">
        {renderContent()}
      </div>
    </div>
  );
}

export default ResultPage;
