// src/components/ResultPage.js
// src/components/ResultPage.js
import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import "../styles/ResultPage.css";

function ResultPage() {
  const [status, setStatus] = useState("processing");

  useEffect(() => {
    setTimeout(() => {
      const matchFound = false; // Simulate match (update this when ML is ready)
      setStatus(matchFound ? "match" : "no-match");
    }, 3000);
  }, []);

  const renderContent = () => {
    if (status === "processing") {
      return "Matching is under process... Please check after some time.";
    } else if (status === "match") {
      return (
        <>
          <h2>Match Found!</h2>
          <p>Name: John Doe</p>
          <p>Organ Type: Kidney</p>
          <p>Location: Mumbai</p>
        </>
      );
    } else {
      return (
        <>
          <h2>No Matches Found</h2>
          <p>
            We know the importance of time and that it is crucial, but currently no matches are
            found. Please check back later.
          </p>
        </>
      );
    }
  };

  return (
    <div className="result-container">
      <motion.div
        className="result-content"
        initial={{ opacity: 0, y: -100 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        {renderContent()}
      </motion.div>
    </div>
  );
}

export default ResultPage;
