import React from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Header.css";

function Header() {
  const { loginWithRedirect, isAuthenticated, logout, user } = useAuth0();
  const navigate = useNavigate();

  // Replace this with how you're storing donorId in the frontend
  const donorId = localStorage.getItem("donorId"); // or use from global state

  const handleDeleteAccount = async () => {
    if (!donorId) {
      alert("Donor ID not found!");
      return;
    }

    try {
      const confirmed = window.confirm("Are you sure you want to delete your account?");
      if (!confirmed) return;

      await axios.delete("http://localhost:8080/delete-user", {
        data: { donorId },
      });

      alert("Account deleted successfully.");
      logout({ returnTo: window.location.origin }); // logs out from Auth0
    } catch (err) {
      console.error("Error deleting account:", err);
      alert("Error deleting account. Try again.");
    }
  };

  return (
    <header className="header">
      <h1 className="header-logo" onClick={() => navigate("/")}>Organ Tracker</h1>
      <nav className="header-nav">
        {isAuthenticated && <span className="user-welcome">Hi, {user.name}!</span>}
        <button onClick={() => navigate("/")} className="nav-btn">Home</button>
        <button onClick={() => navigate("/about")} className="nav-btn">About</button>
        <button onClick={() => navigate("/contact")} className="nav-btn">Contact Us</button>

        {!isAuthenticated ? (
          <>
            <button
              onClick={() =>
                loginWithRedirect({
                  authorizationParams: {
                    screen_hint: "signup",
                    redirect_uri: window.location.origin + "/form",
                  },
                })
              }
              className="auth-btn"
            >
              Sign Up
            </button>

            <button
              onClick={() =>
                loginWithRedirect({
                  authorizationParams: {
                    redirect_uri: window.location.origin + "/result",
                  },
                })
              }
              className="auth-btn"
            >
              Login
            </button>
          </>
        ) : (
          <>
            <button onClick={() => logout({ returnTo: window.location.origin })} className="auth-btn">
              Logout
            </button>

            <button onClick={handleDeleteAccount} className="auth-btn danger-btn">
              Delete Account
            </button>
          </>
        )}
      </nav>
    </header>
  );
}

export default Header;
