import React, { useState } from "react";
import { ethers } from "ethers";
import "./Signup.css"; 
const contractAddress = 0x7C67e3813Da3D646FC8d34eA5e4422238ceb741F; 
const abi = [ 
  {
    "inputs": [
      { "internalType": "uint256", "name": "_age", "type": "uint256" },
      { "internalType": "string", "name": "_hlaTyping", "type": "string" },
      { "internalType": "uint256", "name": "_bmi", "type": "uint256" },
      { "internalType": "string", "name": "_medicalDataHash", "type": "string" }
    ],
    "name": "registerUser",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  }
];

function Signup() {
  const [formData, setFormData] = useState({
    age: "",
    hlaTyping: "",
    bmi: "",
    medicalData: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const registerUser = async () => {
    if (!window.ethereum) return alert("MetaMask is required!");

    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send("eth_requestAccounts", []);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(contractAddress, abi, signer);

      const medicalDataHash = ethers.utils.keccak256(
        ethers.utils.toUtf8Bytes(formData.medicalData)
      ); // Hashing data

      const tx = await contract.registerUser(
        formData.age,
        formData.hlaTyping,
        formData.bmi,
        medicalDataHash
      );
      await tx.wait();
      alert("User Registered on Blockchain!");
    } catch (error) {
      console.error("Registration Failed:", error);
    }
  };

  return (
    <div className="signup-container">
      <h2>Sign Up</h2>
      <input type="number" name="age" placeholder="Age" onChange={handleChange} required />
      <input type="text" name="hlaTyping" placeholder="HLA Typing" onChange={handleChange} required />
      <input type="number" name="bmi" placeholder="BMI" onChange={handleChange} required />
      <input type="text" name="medicalData" placeholder="Medical Data (hashed)" onChange={handleChange} required />
      <button onClick={registerUser}>Register on Blockchain</button>
    </div>
  );
}

export default Signup;
