// src/components/About.js
import React, { useEffect } from 'react';
import './About.css';
import aboutImage from '../images/organ-donation-day.jpg'; // Make sure to update the path to your image

function About() {
  useEffect(() => {
    const handleScroll = () => {
      const aboutSection = document.getElementById('about');
      if (!aboutSection) return; // Prevent errors if the section isn't found
  
      const aboutContent = document.querySelector('.about-content');
      const aboutImage = document.querySelector('.about-image');
  
      const rect = aboutSection.getBoundingClientRect();
      if (rect.top < window.innerHeight && rect.bottom >= 0) {
        aboutContent?.classList.add('slide-in-right');
        aboutImage?.classList.add('slide-in-left');
      } else {
        aboutContent?.classList.remove('slide-in-right');
        aboutImage?.classList.remove('slide-in-left');
      }
    };
  
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);
  
}

export default About;
