import React, { useEffect } from 'react';
import './About.css';
import aboutImage from '../images/organ-donation-day.jpg'; 
import compatibilityImage from '../images/compatibility-check.jpg';
import blockchainImage from '../images/blockchain-image.jpg'; // Add an image for Blockchain section

function About() {
  useEffect(() => {
    const handleScroll = () => {
      const aboutSection = document.getElementById('about');
      const compatibilitySection = document.getElementById('compatibility');
      const blockchainSection = document.getElementById('blockchain');

      if (!aboutSection || !compatibilitySection || !blockchainSection) return;

      const aboutContent = document.querySelector('.about-content');
      const aboutImage = document.querySelector('.about-image');

      const compatibilityContent = document.querySelector('.compatibility-content');
      const compatibilityImage = document.querySelector('.compatibility-image');

      const blockchainContent = document.querySelector('.blockchain-content');
      const blockchainImage = document.querySelector('.blockchain-image');

      const aboutRect = aboutSection.getBoundingClientRect();
      const compatibilityRect = compatibilitySection.getBoundingClientRect();
      const blockchainRect = blockchainSection.getBoundingClientRect();

      if (aboutRect.top < window.innerHeight && aboutRect.bottom >= 0) {
        aboutContent?.classList.add('slide-in-right');
        aboutImage?.classList.add('slide-in-left');
      }

      if (compatibilityRect.top < window.innerHeight && compatibilityRect.bottom >= 0) {
        compatibilityContent?.classList.add('slide-in-left');
        compatibilityImage?.classList.add('slide-in-right');
      }

      if (blockchainRect.top < window.innerHeight && blockchainRect.bottom >= 0) {
        blockchainContent?.classList.add('slide-in-right');
        blockchainImage?.classList.add('slide-in-left');
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <>
      {/* About Organ Tracker Section */}
      <section id="about" className="about">
        <div className="about-content">
          <h3>About Organ Tracker</h3>
          <p>Organ Tracker ensures a secure and transparent organ donation process using blockchain and AI.</p>
        </div>
        <div className="about-image-container">
          <img src={aboutImage} alt="Organ Donation" className="about-image" />
        </div>
      </section>

      {/* Organ Compatibility Section */}
      <section id="compatibility" className="compatibility">
        <div className="compatibility-image-container">
          <img src={compatibilityImage} alt="Compatibility Check" className="compatibility-image" />
        </div>
        <div className="compatibility-content">
          <h3>Organ Compatibility</h3>
          <p>Our Machine Learning algorithms ensure that each organ matches the recipientâ€™s medical criteria for a higher chance of transplantation success.</p>
        </div>
      </section>

      {/* Blockchain for Secure Tracking Section */}
      <section id="blockchain" className="blockchain">
        <div className="blockchain-content">
          <h3>Blockchain for Secure Tracking</h3>
          <p>Blockchain provides a secure, transparent, and immutable ledger for tracking organ donations from donor to recipient.</p>
        </div>
        <div className="blockchain-image-container">
          <img src={blockchainImage} alt="Blockchain Tracking" className="blockchain-image" />
        </div>
      </section>
    </>
  );
}

export default About;
